import React from "react";
import "./WorkItem.css";
import { Col, Container, Row } from "react-bootstrap";
import { Link } from "react-router-dom";
function WorkItem({ title, type, thumbnail }) {
  function handleClick(event) {
    const inputValue = parseInt(event.target.value);
    changeInput(inputValue);
  }

  return (
    <Link to={`/projects/2`} className="grid-item">
      <Row
        className="upper-item"
        style={{
          backgroundImage: `url("data:image/jpeg;base64,${thumbnail}")`,
        }}

        //     background:url(data:image/jpeg;base64,${thumbnail} )
      ></Row>
      <Row className="lower-item">
        <div className="work-item-heading">{type}</div>
        <div className="work-item-description">{title}</div>
      </Row>
    </Link>
  );
}

export default WorkItem;
