function App() {
  // const imageContainer = document.querySelector(".image-column");

  // imageContainer.addEventListener("mouseenter", () => {
  //   imageContainer.classList.add("hovered");
  // });

  // imageContainer.addEventListener("mouseleave", () => {
  //   imageContainer.classList.remove("hovered");
  // });

  const [cursorPosition, setCursorPosition] = useState({ x: 0, y: 0 });

  const handleMouseMove = (event) => {
    setCursorPosition({ x: event.clientX, y: event.clientY });
  };

  return (
    <Container fluid>
      <Row className="top-screen parent-row" onMouseMove={handleMouseMove}>
        <Col xs={1} className="black-background">
          {/* Left Section */}
        </Col>
        <Col xs={10} className="image-column background-image ">
          <div
            className="mask-overlay"
            style={{
              width: `${cursorPosition.x * 2}px`,
              height: `${cursorPosition.x * 2}px`,
              top: `${cursorPosition.y - cursorPosition.x}px`,
              left: `${cursorPosition.x}px`,
            }}
          ></div>
          {/* Middle Section */}
        </Col>
        <Col xs={1} className="black-background">
          {/* Right Section */}
        </Col>
      </Row>

      <Row className="lower-screen parent-row">
        <Col xs={4} className="black-background">
          {/* Left Section */}
        </Col>
        <Col xs={4} className="black-background">
          {/* Middle Section */}
        </Col>
        <Col xs={4} className="black-background">
          {/* Right Section */}
        </Col>
      </Row>
    </Container>
  );
}

export default App;
