import React, { useEffect, useState } from "react";
import { Col, Container, Row } from "react-bootstrap";
import { useParams } from "react-router-dom";
import Header from "../header/Header";
import "../App.css";
import "./ProjectPage.css";

function ProjectPage() {
  const { id } = useParams();

  // Use the id in your component logic
  const [workItem, setWorkItem] = useState(null);
  useEffect(() => {
    console.log(id);
    fetch(`http://localhost:8009/projects/${id}`)
      .then((response) => {
        response.json();
        console.log(response.json.thumbnail);
      })
      .then((json) => setWorkItem(json))
      .catch((error) => console.error(error));
  }, [id]);
  return (
    <>
      <Container fluid>
        <Row className="lower-screen parent-row">
          <Col xs={2} className="blue-background">
            {/* Left Section */}
          </Col>
          <Col xs={8} className="image-column blue-background ">
            <Header type="relative" />
          </Col>

          <Col xs={2} className="blue-background">
            {/* Right Section */}
          </Col>
        </Row>

        <Row className="top-screen parent-row">
          <Col xs={2} className="blue-background">
            {/* Left Section */}
          </Col>
          <Col xs={8} className="blue-background">
            <Row>
              <Col xs={6} className="logo" style={{ position: "relative" }}>
                <img
                  src={
                    workItem && workItem.thumbnail
                      ? `data:image/jpeg;base64,${workItem.thumbnail}`
                      : ""
                  }
                  alt="Logo"
                />
                ;
              </Col>
              <Col xs={4}>
                <Row>
                  <div
                    style={{
                      color: "#7F89E2",
                      textAlign: "center",
                      position: "relative",
                    }}
                  >
                    {workItem && workItem.title}
                  </div>
                </Row>

                <Row>
                  <div className="title" style={{ position: "relative" }}>
                    Project test test test
                  </div>
                </Row>
              </Col>
            </Row>
            <Row style={{ position: "relative" }}>
              <div className="description">dfdfdfdfdfdfdfdfdfdfdf</div>
            </Row>
          </Col>
          <Col xs={2} className="blue-background">
            {/* Right Section */}
          </Col>
        </Row>
      </Container>
    </>
  );
}

export default ProjectPage;
