import logo from "./logo.svg";
import "./App.css";
import "bootstrap/dist/css/bootstrap.css";
import { Col, Container, Row } from "react-bootstrap";
import { useState } from "react";

function App() {
  const [cursorPosition, setCursorPosition] = useState({ x: 0, y: 0 });

  const handleMouseMove = (event) => {
    setCursorPosition({
      x: (event.pageX / window.innerWidth) * 100,
      y: (event.pageY / window.innerHeight) * 100,
    });
  };

  return (
    <Container fluid>
      <Row className="top-screen parent-row">
        <Col xs={1} className="black-background">
          {/* Left Section */}
        </Col>
        <Col
          xs={10}
          className="image-column background-image "
          onMouseMove={handleMouseMove}
        >
          {/* Middle Section */}
          <div
            className="spotlight"
            style={{
              backgroundImage: `radial-gradient(circle at ${cursorPosition.x}% ${cursorPosition.y}%, transparent 80px, rgba(0,0,0,0.75) 500px`,
            }}
          ></div>

          <div className="spotlight2">
            <div
              className="spotlight"
              style={{
                backgroundImage: `radial-gradient(circle at ${cursorPosition.x}% ${cursorPosition.y}%, transparent 80px, rgba(0,0,0,0.75) 500px`,
              }}
            ></div>
          </div>
        </Col>
        <Col xs={1} className="black-background">
          {/* Right Section */}
        </Col>
      </Row>

      <Row className="lower-screen parent-row">
        <Col xs={4} className="black-background">
          {/* Left Section */}
        </Col>
        <Col xs={4} className="black-background">
          {/* Middle Section */}
        </Col>
        <Col xs={4} className="black-background">
          {/* Right Section */}
        </Col>
      </Row>
    </Container>
  );
}

export default App;
