import logo from "./logo.svg";
import "./App.css";
import "bootstrap/dist/css/bootstrap.css";
import { Col, Container, Row } from "react-bootstrap";
import { useState } from "react";

function App() {
  const [cursorPosition, setCursorPosition] = useState({ x: 0, y: 0 });
  const spotlightRadius = 80; // Adjust the spotlight radius as needed
  const imageMargin = 50; // Adjust the margin of the image from the edges

  const handleMouseMove = (event) => {
    const x = (event.pageX / window.innerWidth) * 100;
    const y = (event.pageY / window.innerHeight) * 100;
    setCursorPosition({ x, y });
  };

  // Calculate the spotlight position based on cursor position
  const spotlightX = Math.min(
    100 - spotlightRadius - imageMargin,
    Math.max(imageMargin, cursorPosition.x - spotlightRadius)
  );
  const spotlightY = Math.min(
    100 - spotlightRadius - imageMargin,
    Math.max(imageMargin, cursorPosition.y - spotlightRadius)
  );

  return (
    <Container fluid onMouseMove={handleMouseMove}>
      <Row className="top-screen parent-row">
        <Col xs={1} className="black-background"></Col>
        <Col
          xs={10}
          className="image-column background-image"
          style={{ overflow: "hidden" }}
        >
          <div
            className="spotlight"
            style={{
              backgroundImage: `radial-gradient(circle at ${spotlightX}% ${spotlightY}%, transparent 0px, rgba(0,0,0,0.75) ${spotlightRadius}px)`,
            }}
          ></div>
        </Col>
        <Col xs={1} className="black-background"></Col>
      </Row>
    </Container>
  );
}

export default App;
