  const [cursorPosition, setCursorPosition] = useState({ x: 0, y: 0 });

  const handleMouseMove = (event) => {
    setCursorPosition({
      x: (event.pageX / window.innerWidth) * 100,
      y: (event.pageY / window.innerHeight) * 100,
    });
  };

  return (
    <Container fluid>
      <Row className="top-screen parent-row">
        <Col xs={1} className="black-background">
          {/* Left Section */}
        </Col>
        <Col
          xs={10}
          className="image-column background-image "
          onMouseMove={handleMouseMove}
        >
          {
            /* <div
            className="overlay"
            style={{
              clipPath: `circle(100px at ${cursorPosition.x}px ${cursorPosition.y}px)`, // Define a circular clip path centered at the cursor position
