import React, { useEffect, useState } from "react";
import { Col, Container, Row } from "react-bootstrap";
import { useParams } from "react-router-dom";
import Header from "../header/Header";
import "../App.css";
import "./ProjectPage.css";

function ProjectPage() {
  const { id } = useParams();

  // Use the id in your component logic
  const [workItem, setWorkItem] = useState(null);
  useEffect(() => {
    console.log(id);
    fetch(`http://localhost:8009/projects/${id}`)
      .then((response) => response.json())
      .then((json) => setWorkItem(json))
      .catch((error) => console.error(error));
  }, [id]);

  return (
    <>
      <Container fluid style={{ height: "100vh" }}>
        <Row className="parent-row">
          <Col xs={2} className="blue-background">
            {/* Left Section */}
          </Col>
          <Col xs={8} className="blue-background">
            {/* Middle Section */}
            <Row className="d-flex flex-column" style={{ height: "100%" }}>
              <Col xs={6} className="logo" style={{ position: "relative" }}>
                <img
                  src={
                    workItem && workItem.thumbnail
                      ? `data:image/jpeg;base64,${workItem.thumbnail}`
                      : ""
                  }
                  alt="Logo"
                />
              </Col>
              <Col xs={4}>
                <div
                  style={{
                    color: "#7F89E2",
                    position: "relative",
                  }}
                >
                  Project
                </div>
                <div className="title" style={{ position: "relative" }}>
                  {workItem && workItem.title}
                </div>
              </Col>
              <Col xs={12} style={{ position: "relative" }}>
                <div className="description">
                  {/* Your description content */}
                </div>
              </Col>
            </Row>
          </Col>
          <Col xs={2} className="blue-background">
            {/* Right Section */}
          </Col>
        </Row>
      </Container>
    </>
  );
}

export default ProjectPage;
